import { VuiFlexContainer, VuiFlexItem, VuiTitle, VuiText, VuiSpacer } from "../ui";
import { SearchControls } from "./controls/SearchControls";
import { ExampleQuestions } from "./controls/ExampleQuestions";
import { useSearchContext } from "../contexts/SearchContext";
import { AppHeader } from "./chrome/AppHeader";
import { SummaryUx } from "./SummaryUx";
import "./searchView.scss";

export const SearchView = () => {
  const { isSearching, searchError, searchResults, isSummarizing, summarizationError, summarizationResponse } =
    useSearchContext();

  let content;

  if (
    !isSearching &&
    !searchError &&
    !searchResults &&
    !isSummarizing &&
    !summarizationError &&
    !summarizationResponse
  ) {
    content = <ExampleQuestions />;
  } else {
    content = <SummaryUx />;
  }

  return (
    <>
      <AppHeader />
      <VuiFlexContainer className="searchView" direction="column" alignItems="center" spacing="none">
        <VuiFlexItem className="searchHeader">
          <VuiTitle size="l" className="mainTitle">
            <h1>Investment Document Intelligence</h1>
          </VuiTitle>

          <VuiSpacer size="s" />

          <VuiText size="m" className="subtitle">
            <p>Search across your investment documents with AI-powered insights</p>
          </VuiText>

          <VuiSpacer size="l" />

          <VuiFlexItem className="searchControlsContainer">
            <SearchControls hasQuery={Boolean(isSearching || searchResults)} />
          </VuiFlexItem>
        </VuiFlexItem>

        <VuiFlexItem grow={1} className="searchContent" alignItems="start">
          {content}
        </VuiFlexItem>
      </VuiFlexContainer>
    </>
  );
};
