import { useSearchContext } from "../../contexts/SearchContext";
import { VuiFlexContainer, VuiFlexItem, VuiTitle, VuiSpacer } from "../../ui";
import "./categoryQuestions.scss";

// Define question types with FCS rankings
interface Question {
  text: string;
  fcsRank?: number; // Higher number means higher rank
}

// Define category interface
interface Category {
  title: string;
  questions: Question[];
}

// Define the question categories and their questions
const QUESTION_CATEGORIES: Category[] = [
  {
    title: "Market Research",
    questions: [
      { text: "How much is Apple's Research and Development budget?", fcsRank: 72 },
      { text: "How are investors navigating a fully-valued market?", fcsRank: 85 },
      { text: "How do global bond markets compare in 2025?", fcsRank: 78 },
      { text: "How have ESG investments performed in the last five years?", fcsRank: 92 }
    ]
  },
  {
    title: "Financial Analysis",
    questions: [
      { text: "What are the advisory fees charged in the First Light Funds group?", fcsRank: 88 },
      { text: "How does Lipper calculate total expense ratios and their impact on private equity investors?", fcsRank: 79 },
      { text: "What is the historical return of Jupiter Large Cap Fund over the last five years?", fcsRank: 83 }
    ]
  },
  {
    title: "Portfolio Management",
    questions: [
      { text: "What are the largest holdings in the Arcadia 13-F filing?", fcsRank: 81 },
      { text: "What is the net asset value (NAV) of Morgan Stanley's North Haven Real Estate Fund?", fcsRank: 77 },
      { text: "Which fund in the First Light Funds group has the highest exposure to technology stocks?", fcsRank: 94 }
    ]
  }
];

export const CategoryQuestions = () => {
  const { onSearch } = useSearchContext();

  // Sort questions in each category by FCS rank (highest first)
  const sortedCategories: Category[] = QUESTION_CATEGORIES.map(category => ({
    ...category,
    questions: [...category.questions].sort((a: Question, b: Question) => (b.fcsRank || 0) - (a.fcsRank || 0))
  }));

  return (
    <div className="categoryQuestions">
      <VuiFlexContainer spacing="l" justifyContent="spaceBetween">
        {sortedCategories.map((category) => (
          <VuiFlexItem key={category.title} grow={1} className="categoryFlexItem">
            <div className="categoryCard">
              <VuiTitle size="m">
                <h3>{category.title}</h3>
              </VuiTitle>

              <VuiSpacer size="m" />

              <VuiFlexContainer direction="column" spacing="s">
                {category.questions.map((question: Question) => (
                  <VuiFlexItem key={question.text}>
                    <button 
                      className="questionButton"
                      onClick={() => onSearch({ value: question.text })}
                    >
                      <span>{question.text}</span>
                      {question.fcsRank && (
                        <span className="fcsRank">FCS: {question.fcsRank}</span>
                      )}
                    </button>
                  </VuiFlexItem>
                ))}
              </VuiFlexContainer>
            </div>
          </VuiFlexItem>
        ))}
      </VuiFlexContainer>
    </div>
  );
};
