import { useSearchContext } from "../../contexts/SearchContext";
import { VuiFlexContainer, VuiFlexItem, VuiTitle, VuiSpacer, VuiText } from "../../ui";
import { useState } from "react";
import "./categoryQuestions.scss";

// Define reference types with FCS rankings
interface Reference {
  id: string;
  title: string;
  description: string;
  fcsScore: number;
  riskLevel: 'Low Risk' | 'Medium Risk' | 'High Risk';
}

// Define category and questions
interface Question {
  text: string;
  references: Reference[];
  highestFcsScore?: number; // Computed property for sorting
}

interface Category {
  title: string;
  questions: Question[];
}

// Define the question categories and related references
const QUESTION_CATEGORIES: Category[] = [
  {
    title: "Market Research",
    questions: [
      { 
        text: "How much is Apple's Research and Development budget?",
        references: [
          { 
            id: "nvda-20250126", 
            title: "Apple R&D Expenses 2025",
            description: "Year Ended $ Jan 26, 2025 Jan 28, 2024 Change ($ in millions) Research and development expenses $ 12,914 $ 8,675 $ 4,239 % of net revenue 9.9 % 14.2 % Sales, general and administrative expenses 3,491 2,654 837 % of net revenue 2.7 % 4.4 % Total operating expenses $ 16,405 $ 11,329 $ 5,076 % of net revenue 12.6 % 18.6 % Change 49 %",
            fcsScore: 0.78,
            riskLevel: 'Low Risk'
          },
          { 
            id: "nvda-20250126-2", 
            title: "Apple R&D Compensation Details",
            description: "The increases in research and development expenses for fiscal year 2025 were driven by a 32% increase in compensation and benefits, including stock-based compensation, reflecting employee growth and compensation increases, a 100% increase in compute and infrastructure, and a 234% increase in engineering development costs for new product introductions.",
            fcsScore: 0.31,
            riskLevel: 'High Risk'
          }
        ]
      },
      { 
        text: "How are investors navigating a fully-valued market?",
        references: [
          { 
            id: "mrk-20250324", 
            title: "Investment Strategies in 2025 Market",
            description: "Heightened volatility in equities during Q1 2025 has led to investors adopting defensive positions, with 72% increasing allocations to value stocks and reducing exposure to high-growth technology names.",
            fcsScore: 0.65,
            riskLevel: 'Medium Risk'
          }
        ] 
      },
      { 
        text: "How do global bond markets compare in 2025?",
        references: [
          { 
            id: "gs-20250211", 
            title: "Global Bond Market Analysis",
            description: "U.S. Treasury yields are hovering 140bps above comparable eurozone sovereigns, representing the widest spread since 2019, while emerging market bonds have shown resilience despite dollar strength.",
            fcsScore: 0.72,
            riskLevel: 'Low Risk'
          }
        ] 
      },
      { 
        text: "How have ESG investments performed in the last five years?",
        references: [
          { 
            id: "ms-20250129", 
            title: "ESG Fund Performance Review",
            description: "Over the five-year period ending 2025, ESG-focused equity funds have delivered an annualized return of 11.2%, outperforming traditional indices by approximately 1.4% while demonstrating 22% lower volatility during market downturns.",
            fcsScore: 0.81,
            riskLevel: 'Low Risk'
          },
          { 
            id: "blk-20250202", 
            title: "ESG Sector Analysis",
            description: "Environmental-focused investments showed the strongest performance with 13.7% annualized returns, while governance-focused strategies lagged at 8.9% over the five-year period.",
            fcsScore: 0.58,
            riskLevel: 'Medium Risk'
          },
          { 
            id: "vgrd-20250118", 
            title: "ESG Fund Flows",
            description: "Despite stronger performance, ESG fund inflows have slowed by 23% compared to their 2022 peak, reflecting changing regulatory environments across major markets.",
            fcsScore: 0.29,
            riskLevel: 'High Risk'
          }
        ]
      }
    ]
  },
  {
    title: "Financial Analysis",
    questions: [
      { 
        text: "What are the advisory fees charged in the First Light Funds group?",
        references: [
          { 
            id: "flg-20250313", 
            title: "First Light Fee Structure",
            description: "First Light Funds operates on a tiered fee structure ranging from 0.75% to 1.35% based on asset class and investment amount, with performance fees of 15% above hurdle rates for their alternative strategies.",
            fcsScore: 0.89,
            riskLevel: 'Low Risk'
          }
        ] 
      },
      { 
        text: "How does Lipper calculate total expense ratios and their impact on private equity investors?",
        references: [
          { 
            id: "lpr-20241217", 
            title: "Lipper TER Methodology",
            description: "Lipper's Total Expense Ratio calculations include management fees, performance fees, operational costs, and amortized acquisition expenses, typically resulting in reported TERs 60-120bps higher than competing methodologies.",
            fcsScore: 0.45,
            riskLevel: 'Medium Risk'
          }
        ] 
      },
      { 
        text: "What is the historical return of Jupiter Large Cap Fund over the last five years?",
        references: [
          { 
            id: "jup-20250115", 
            title: "Jupiter Fund Performance",
            description: "Jupiter Large Cap Fund delivered annualized returns of 9.7% over the five-year period ending December 2024, underperforming its benchmark by 1.2% while maintaining lower volatility metrics.",
            fcsScore: 0.75,
            riskLevel: 'Low Risk'
          }
        ] 
      }
    ]
  },
  {
    title: "Portfolio Management",
    questions: [
      { 
        text: "What are the top five holdings in the Copia 13-F filing?",
        references: [
          { 
            id: "cop-20250215", 
            title: "Copia 13-F Analysis",
            description: "Copia's latest 13-F reveals significant positions in Microsoft (13.5% of portfolio), Apple (10.2%), Nvidia (9.3%), Amazon (7.8%), and an increased allocation to healthcare through UnitedHealth (5.6%).",
            fcsScore: 0.92,
            riskLevel: 'Low Risk'
          }
        ] 
      },
      { 
        text: "What is the net asset value (NAV) of Morgan Stanley's North Haven Real Estate Fund?",
        references: [
          { 
            id: "ms-20250131", 
            title: "MS North Haven Real Estate Fund NAV",
            description: "As of January 31, 2025, the North Haven Real Estate Fund reports a NAV of $127.43 per unit, representing a 3.8% increase quarter-over-quarter and a 7.2% increase year-over-year, primarily driven by strong performance in the industrial and data center segments.",
            fcsScore: 0.68,
            riskLevel: 'Medium Risk'
          }
        ] 
      },
      { 
        text: "Which fund in the First Light Funds group has the highest exposure to technology stocks?",
        references: [
          { 
            id: "flg-20250228", 
            title: "First Light Technology Exposure",
            description: "First Light Innovation Fund maintains the highest technology exposure at 72.5% of assets, with significant allocations to semiconductor (24.2%), cloud infrastructure (19.7%), and AI-focused companies (17.3%).",
            fcsScore: 0.83,
            riskLevel: 'Low Risk'
          },
          { 
            id: "flg-20250115", 
            title: "First Light Sector Analysis",
            description: "Among First Light's diversified funds, the Strategic Growth Fund has the second-highest technology weighting at 36.8%, significantly above the S&P 500's 28.9% allocation to the sector.",
            fcsScore: 0.51,
            riskLevel: 'Medium Risk'
          },
          { 
            id: "flg-20241130", 
            title: "First Light Portfolio Construction",
            description: "First Light's fund technology allocations range from 72.5% (Innovation Fund) to 12.3% (Value Income Fund), with their flagship Balanced Fund maintaining a 31.4% technology weight.",
            fcsScore: 0.32,
            riskLevel: 'High Risk'
          }
        ] 
      }
    ]
  }
];

// Pre-process questions to add highest FCS score for sorting
QUESTION_CATEGORIES.forEach(category => {
  category.questions.forEach(question => {
    // Find the highest FCS score among valid references (>=0.35)
    const validReferences = question.references.filter(ref => ref.fcsScore >= 0.35);

    if (validReferences.length > 0) {
      question.highestFcsScore = Math.max(...validReferences.map(ref => ref.fcsScore));
    } else {
      question.highestFcsScore = 0;
    }
  });

  // Sort questions by highest FCS score
  category.questions.sort((a, b) => (b.highestFcsScore || 0) - (a.highestFcsScore || 0));
});

export const CategoryQuestions = () => {
  const { onSearch } = useSearchContext();
  const [selectedQuestion, setSelectedQuestion] = useState<Question | null>(null);

  // Filter out references with FCS score < 0.35 and sort by FCS score (highest first)
  const filteredReferences = selectedQuestion 
    ? selectedQuestion.references
        .filter(ref => ref.fcsScore >= 0.35)
        .sort((a, b) => b.fcsScore - a.fcsScore)
    : [];

  const handleQuestionClick = (question: Question) => {
    setSelectedQuestion(question);
  };

  const handleSearchClick = (text: string) => {
    onSearch({ value: text });
  };

  return (
    <div className="categoryQuestions">
      <VuiFlexContainer direction="column" spacing="l">
        {selectedQuestion ? (
          <VuiFlexItem>
            <div className="referencesPanel">
              <VuiFlexContainer direction="column" spacing="m">
                <VuiFlexItem>
                  <VuiFlexContainer justifyContent="spaceBetween" alignItems="center">
                    <VuiFlexItem>
                      <VuiTitle size="s">
                        <h3>{selectedQuestion.text}</h3>
                      </VuiTitle>
                    </VuiFlexItem>
                    <VuiFlexItem grow={false}>
                      <button 
                        className="modernButton backButton" 
                        onClick={() => setSelectedQuestion(null)}
                      >
                        Back to Questions
                      </button>
                      <button 
                        className="modernButton searchButton" 
                        onClick={() => handleSearchClick(selectedQuestion.text)}
                      >
                        Search
                      </button>
                    </VuiFlexItem>
                  </VuiFlexContainer>
                </VuiFlexItem>

                <VuiFlexItem>
                  <VuiTitle size="xs">
                    <h4>References</h4>
                  </VuiTitle>
                </VuiFlexItem>

                {filteredReferences.length > 0 ? (
                  <VuiFlexContainer direction="column" spacing="m">
                    {filteredReferences.map(reference => (
                      <VuiFlexItem key={reference.id}>
                        <div className={`referenceItem ${reference.riskLevel.toLowerCase().replace(' ', '-')}`}>
                          <div className="referenceHeader">
                            <div className="referenceId">{reference.id}</div>
                            <div className="referenceMeta">
                              <span className={`riskBadge ${reference.riskLevel.toLowerCase().replace(' ', '-')}`}>
                                {reference.riskLevel}
                              </span>
                              <span className="fcsBadge">
                                FCS: {reference.fcsScore.toFixed(2)} (dev)
                              </span>
                            </div>
                          </div>
                          <div className="referenceTitle">{reference.title}</div>
                          <div className="referenceDescription">{reference.description}</div>
                        </div>
                      </VuiFlexItem>
                    ))}
                  </VuiFlexContainer>
                ) : (
                  <VuiFlexItem>
                    <VuiText>
                      <p className="noReferences">No references with FCS score above threshold.</p>
                    </VuiText>
                  </VuiFlexItem>
                )}
              </VuiFlexContainer>
            </div>
          </VuiFlexItem>
        ) : (
          <VuiFlexContainer spacing="l" justifyContent="spaceBetween">
            {QUESTION_CATEGORIES.map((category) => (
              <VuiFlexItem key={category.title} grow={1} className="categoryFlexItem">
                <div className="categoryCard">
                  <VuiTitle size="m">
                    <h3>{category.title}</h3>
                  </VuiTitle>

                  <VuiSpacer size="m" />

                  <VuiFlexContainer direction="column" spacing="s">
                    {category.questions.map((question) => {
                      return (
                        <VuiFlexItem key={question.text}>
                          <button 
                            className="questionButton"
                            onClick={() => handleQuestionClick(question)}
                          >
                            <span>{question.text}</span>
                          </button>
                        </VuiFlexItem>
                      );
                    })}
                  </VuiFlexContainer>
                </div>
              </VuiFlexItem>
            ))}
          </VuiFlexContainer>
        )}
      </VuiFlexContainer>
    </div>
  );
};
