import { VuiFlexContainer, VuiFlexItem, VuiTitle, VuiText } from "../../ui";
import { HeaderLogo } from "./HeaderLogo";
import { Link, useLocation } from "react-router-dom";
import "./appHeader.scss";

export const AppHeader = () => {
  const location = useLocation();
  const isHarvardPage = location.pathname === "/harvard-management";

  return (
    <header className="threadneedleHeader">
      <div className="headerContent">
        <VuiFlexContainer direction="column" alignItems="center" spacing="s">
          <VuiFlexItem>
            <HeaderLogo />
          </VuiFlexItem>

          <VuiFlexItem>
            <VuiTitle size="l" className="headerTitle">
              <h1>Enterprise Intelligence</h1>
            </VuiTitle>
          </VuiFlexItem>

          <VuiFlexItem>
            <VuiText size="m" className="headerSubtitle">
              <p>Access insights across your investment portfolio and market research</p>
            </VuiText>
          </VuiFlexItem>

          <VuiFlexItem>
            <nav className="headerNav">
              <Link to="/" className={`navLink ${!isHarvardPage ? 'active' : ''}`}>
                Home
              </Link>
              <Link to="/harvard-management" className={`navLink ${isHarvardPage ? 'active' : ''}`}>
                Harvard Management Documents
              </Link>
            </nav>
          </VuiFlexItem>
        </VuiFlexContainer>
      </div>
    </header>
  );
};
