import { useSearchContext } from "../../contexts/SearchContext";
import { VuiFlexContainer, VuiFlexItem, VuiTitle, VuiText, VuiSpacer, VuiHorizontalRule } from "../../ui";
import "./categoryQuestions.scss";

// Define the question categories and their questions
const QUESTION_CATEGORIES = [
  {
    title: "Market Research",
    questions: [
      "How much is Apple's Research and Development budget?",
      "How are investors navigating a fully-valued market?",
      "How do global bond markets compare in 2025?",
      "How have ESG investments performed in the last five years?"
    ]
  },
  {
    title: "Financial Analysis",
    questions: [
      "What are the advisory fees charged in the First Light Funds group?",
      "How does Lipper calculate total expense ratios and their impact on private equity investors?",
      "What is the historical return of Jupiter Large Cap Fund over the last five years?"
    ]
  },
  {
    title: "Portfolio Management",
    questions: [
      "What are the largest holdings in the Arcadia 13-F filing?",
      "What is the net asset value (NAV) of Morgan Stanley's North Haven Real Estate Fund?",
      "Which fund in the First Light Funds group has the highest exposure to technology stocks?"
    ]
  }
];

export const CategoryQuestions = () => {
  const { onSearch } = useSearchContext();

  // Current timestamp for "Last updated" display
  const currentDate = new Date();
  const formattedDate = currentDate.toLocaleDateString('en-US', {
    month: 'long',
    day: 'numeric',
    year: 'numeric'
  });

  return (
    <div className="categoryQuestions">
      <VuiFlexContainer direction="column" spacing="l">
        {/* Header */}
        <VuiFlexItem>
          <VuiTitle size="l" className="enterpriseTitle">
            <h2>Enterprise Intelligence</h2>
          </VuiTitle>

          <VuiSpacer size="s" />

          <VuiText size="m" className="enterpriseSubtitle">
            <p>Access insights across your investment portfolio and market research</p>
          </VuiText>
        </VuiFlexItem>

        {/* Tabs */}
        <VuiFlexItem>
          <VuiFlexContainer spacing="m" className="tabs">
            <VuiFlexItem>
              <button className="tabButton active">All Categories</button>
            </VuiFlexItem>
            <VuiFlexItem grow={1}></VuiFlexItem>
            <VuiFlexItem>
              <VuiText size="s">
                <p className="lastUpdated">Last updated: {formattedDate}</p>
              </VuiText>
            </VuiFlexItem>
          </VuiFlexContainer>

          <VuiSpacer size="s" />
          <VuiHorizontalRule />
          <VuiSpacer size="m" />
        </VuiFlexItem>

        {/* Question Categories */}
        <VuiFlexItem>
          <VuiFlexContainer spacing="l" justifyContent="spaceBetween">
            {QUESTION_CATEGORIES.map((category) => (
              <VuiFlexItem key={category.title} grow={1} className="categoryFlexItem">
                <div className="categoryCard">
                  <VuiTitle size="m">
                    <h3>{category.title}</h3>
                  </VuiTitle>

                  <VuiSpacer size="m" />

                  <VuiFlexContainer direction="column" spacing="s">
                    {category.questions.map((question) => (
                      <VuiFlexItem key={question}>
                        <button 
                          className="questionButton"
                          onClick={() => onSearch({ value: question })}
                        >
                          {question}
                        </button>
                      </VuiFlexItem>
                    ))}
                  </VuiFlexContainer>
                </div>
              </VuiFlexItem>
            ))}
          </VuiFlexContainer>
        </VuiFlexItem>
      </VuiFlexContainer>
    </div>
  );
};
