import React, { useState, useEffect } from 'react';
import { VuiFlexContainer, VuiFlexItem, VuiTitle, VuiText, VuiSpacer } from '../../ui';
import { AppHeader } from '../chrome/AppHeader';
import './harvardManagementDocuments.scss';
import { useSearchContext } from '../../contexts/SearchContext';

// Define document type
interface Document {
  id: string;
  title: string;
  date: string;
  description: string;
  url?: string;
}

// Sample Harvard Management documents
// In a production environment, these would be fetched from an API
const HARVARD_MANAGEMENT_DOCUMENTS: Document[] = [
  {
    id: "hm-20250412",
    title: "Harvard Endowment Performance Review Q1 2025",
    date: "April 12, 2025",
    description: "Quarterly performance analysis of Harvard Management Company's endowment portfolio, highlighting sector allocation and benchmark comparisons.",
    url: "/documents/harvard/endowment-q1-2025.pdf"
  },
  {
    id: "hm-20250328",
    title: "ESG Integration in Fixed Income Strategies",
    date: "March 28, 2025",
    description: "Research paper on Harvard Management's approach to incorporating ESG factors in fixed income investment decisions and impact on risk-adjusted returns.",
    url: "/documents/harvard/esg-fixed-income-2025.pdf"
  },
  {
    id: "hm-20250215",
    title: "Alternative Investment Allocation Strategy",
    date: "February 15, 2025",
    description: "Strategic framework for Harvard Management's allocation to alternative investments including private equity, venture capital, and real assets.",
    url: "/documents/harvard/alt-investment-strategy-2025.pdf"
  },
  {
    id: "hm-20250129",
    title: "Global Macro Economic Outlook",
    date: "January 29, 2025",
    description: "Harvard Management's analysis of global economic trends and their potential impact on investment strategies for the coming fiscal year.",
    url: "/documents/harvard/global-macro-outlook-2025.pdf"
  },
  {
    id: "hm-20250110",
    title: "Emerging Markets Debt Analysis",
    date: "January 10, 2025",
    description: "In-depth review of emerging market debt opportunities and risks, with country-specific assessments and allocation recommendations.",
    url: "/documents/harvard/em-debt-analysis-2025.pdf"
  },
  {
    id: "hm-20241215",
    title: "Climate Risk Assessment Framework",
    date: "December 15, 2024",
    description: "Harvard Management's methodology for evaluating climate-related risks across asset classes and integration into the portfolio construction process.",
    url: "/documents/harvard/climate-risk-framework-2024.pdf"
  },
  {
    id: "hm-20241128",
    title: "Quarterly Asset Allocation Review",
    date: "November 28, 2024",
    description: "Review of Harvard endowment's strategic and tactical asset allocation decisions and performance attribution for Q4 2024.",
    url: "/documents/harvard/q4-asset-allocation-2024.pdf"
  },
  {
    id: "hm-20241105",
    title: "Innovation in Healthcare Investments",
    date: "November 5, 2024",
    description: "Sector-specific research on healthcare innovation trends and investment opportunities identified by Harvard Management analysts.",
    url: "/documents/harvard/healthcare-innovation-2024.pdf"
  }
];

export const HarvardManagementDocuments = () => {
  const { onSearch } = useSearchContext();
  const [documents, setDocuments] = useState<Document[]>(HARVARD_MANAGEMENT_DOCUMENTS);
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');
  const [searchTerm, setSearchTerm] = useState('');

  // Handle document search
  useEffect(() => {
    if (searchTerm === '') {
      setDocuments(HARVARD_MANAGEMENT_DOCUMENTS);
    } else {
      const filtered = HARVARD_MANAGEMENT_DOCUMENTS.filter(doc => 
        doc.title.toLowerCase().includes(searchTerm.toLowerCase()) || 
        doc.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setDocuments(filtered);
    }
  }, [searchTerm]);

  // Handle sort order change
  const handleSortChange = () => {
    const newOrder = sortOrder === 'desc' ? 'asc' : 'desc';
    setSortOrder(newOrder);

    const sorted = [...documents].sort((a, b) => {
      const dateA = new Date(a.date);
      const dateB = new Date(b.date);
      return newOrder === 'desc' ? 
        dateB.getTime() - dateA.getTime() : 
        dateA.getTime() - dateB.getTime();
    });

    setDocuments(sorted);
  };

  // Handle document click
  const handleDocumentClick = (document: Document) => {
    if (document.title) {
      onSearch({ value: document.title });
    }
  };

  return (
    <div className="harvardManagementPage">
      <AppHeader />

      <div className="harvardManagementDocuments">
        <VuiFlexContainer direction="column" spacing="l">
          <VuiFlexItem>
            <VuiFlexContainer justifyContent="spaceBetween" alignItems="center">
              <VuiFlexItem>
                <VuiTitle size="m">
                  <h2>Harvard Management Documents</h2>
                </VuiTitle>
              </VuiFlexItem>
              <VuiFlexItem grow={false}>
                <div className="documentControls">
                  <input 
                    type="text" 
                    placeholder="Search documents..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="documentSearchInput"
                  />
                  <button 
                    className="modernButton sortButton" 
                    onClick={handleSortChange}
                  >
                    Sort by Date {sortOrder === 'desc' ? '(Newest First)' : '(Oldest First)'}
                  </button>
                </div>
              </VuiFlexItem>
            </VuiFlexContainer>
          </VuiFlexItem>

          <VuiSpacer size="m" />

          {documents.length > 0 ? (
            <VuiFlexContainer direction="column" spacing="m">
              {documents.map(doc => (
                <VuiFlexItem key={doc.id}>
                  <div className="documentItem" onClick={() => handleDocumentClick(doc)}>
                    <div className="documentHeader">
                      <div className="documentId">{doc.id}</div>
                      <div className="documentDate">{doc.date}</div>
                    </div>
                    <div className="documentTitle">{doc.title}</div>
                    <div className="documentDescription">{doc.description}</div>
                  </div>
                </VuiFlexItem>
              ))}
            </VuiFlexContainer>
          ) : (
            <VuiFlexItem>
              <VuiText>
                <p className="noDocuments">No documents matching your search criteria.</p>
              </VuiText>
            </VuiFlexItem>
          )}
        </VuiFlexContainer>
      </div>
    </div>
  );
};
