import { useState, useEffect } from "react";
import { VuiBadge, VuiFlexContainer, VuiFlexItem, VuiText, VuiTextColor } from "../../ui";
import { SearchResultWithSnippet } from "../types";
import { HfInference } from "@huggingface/inference";
import { useConfigContext } from "../../contexts/ConfigurationContext";

// Known confidence levels for hallucination scores
export type ConfidenceLevel = "unavailable" | "low" | "medium" | "high";

// Map a numerical score to a confidence level
const getConfidenceLevel = (score: number): ConfidenceLevel => {
  if (score < 0) {
    return "unavailable";
  }
  if (score <= 0.33) {
    return "low";
  }
  if (score <= 0.66) {
    return "medium";
  }
  return "high";
};

// Get color and label for the badge based on confidence level
const getBadgeProps = (
  confidenceLevel: ConfidenceLevel
): { color: "success" | "warning" | "danger" | "neutral"; label: string } => {
  switch (confidenceLevel) {
    case "low":
      return { color: "success", label: "Low Risk" };
    case "medium":
      return { color: "warning", label: "Medium Risk" };
    case "high":
      return { color: "danger", label: "High Risk" };
    case "unavailable":
    default:
      return { color: "neutral", label: "Score N/A" };
  }
};

interface ResultHallucinationScoreProps {
  result: SearchResultWithSnippet;
}

export const ResultHallucinationScore = ({ result }: ResultHallucinationScoreProps) => {
  const { search } = useConfigContext();
  const [hallucinationScore, setHallucinationScore] = useState<number>(-1);
  const [isLoading, setIsLoading] = useState<boolean>(false);

  useEffect(() => {
    const evaluateHallucination = async () => {
      if (!search.hfToken) {
        console.warn("Hugging Face token not provided. Cannot evaluate hallucination.");
        return;
      }

      setIsLoading(true);
      try {
        const inference = new HfInference(search.hfToken);
        const API_URL = "https://api-inference.huggingface.co/models/vectara/hallucination_evaluation_model";
        const hem = inference.endpoint(API_URL);

        // Combine the result text for evaluation
        const { snippet } = result;
        const inputText = `${snippet.pre} ${snippet.text} ${snippet.post}`;

        const responseData = await hem.textClassification({ inputs: inputText });
        const score = Math.round(responseData[0].score * 100) / 100; // Round to 2 decimal places
        setHallucinationScore(score);
      } catch (error) {
        console.error("Error evaluating hallucination:", error);
        setHallucinationScore(-1);
      } finally {
        setIsLoading(false);
      }
    };

    evaluateHallucination();
  }, [result, search.hfToken]);

  const confidenceLevel = getConfidenceLevel(hallucinationScore);
  const { color, label } = getBadgeProps(confidenceLevel);

  return (
    <VuiFlexContainer alignItems="center" spacing="xs">
      <VuiFlexItem grow={false} shrink={false}>
        <VuiBadge color={color}>
          {isLoading ? "Evaluating..." : label}
        </VuiBadge>
      </VuiFlexItem>
      {hallucinationScore >= 0 && !isLoading && (
        <VuiFlexItem grow={false} shrink={false}>
          <VuiText size="xs">
            <p>
              <VuiTextColor color="subdued">
                Score: {hallucinationScore.toFixed(2)}
              </VuiTextColor>
            </p>
          </VuiText>
        </VuiFlexItem>
      )}
    </VuiFlexContainer>
  );
};
