import { ChangeEvent, FormEvent, useState } from "react";
import { BiSearch } from "react-icons/bi";
import {
  VuiFlexContainer,
  VuiFlexItem,
  VuiSearchInput,
  VuiButton,
} from "../../ui";
import { useSearchContext } from "../../contexts/SearchContext";
import "./searchControls.scss";

type Props = {
  hasQuery: boolean;
};

export const SearchControls = ({ hasQuery }: Props) => {
  const { searchValue, setSearchValue, onSearch } = useSearchContext();

  const onSearchChange = (event: ChangeEvent<HTMLInputElement>) => {
    setSearchValue(event.target.value);
  };

  const onSearchSubmit = async (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    onSearch({ value: searchValue });
  };

  return (
    <div className="searchControls">
      <form onSubmit={onSearchSubmit} className="searchForm">
        <VuiFlexContainer alignItems="center">
          <VuiFlexItem grow={1}>
            <VuiSearchInput
              size="l"
              value={searchValue}
              onChange={onSearchChange}
              placeholder="Ask a question about your investment documents..."
              autoFocus
              buttonText={null}
            />
          </VuiFlexItem>

          <VuiFlexItem grow={0} shrink={0}>
            <VuiButton
              color="primary"
              size="l"
              type="submit"
              className="searchButton"
              iconPosition="right"
              icon={<BiSearch size={24} />}
            >
              Search
            </VuiButton>
          </VuiFlexItem>
        </VuiFlexContainer>
      </form>
    </div>
  );
};
